import React from "react";
import Transaction from "./Transaction";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function TransactionsList({trans}) {
  const [search, setSearch] = useState("");

  const itemsToDisplay = trans
    // category
    .filter(
      (item) => selectedCategory === "All" || item.category === selectedCategory
    )
    // search term
    .filter((tran) => tran.name.toLowerCase().includes(search.toLowerCase()));

  return (
    <div>
      <Search />
      <AddTransactionForm />
      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th>
              <h3 className="ui center aligned header">Date</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Category</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Amount</h3>
            </th>
          </tr>
          {/* Included the Transaction component */}
          { trans.map((posted)=>(
            <Transaction key={posted.id} date={posted.date} desc={posted.description} 
            categ={posted.category} amount={posted.amount} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
